import React from 'react';
import {Link} from 'react-router-dom';
import PropTypes from 'prop-types';

import './nav-bar.css';

export default function NavBar({isAuth, logout}) {

    const renderProfile = () => {
        return (
            <li className="nav-item mr-2">
                <Link to='/profile' data-value="profile" className="nav-link" title="Profile">
                    <i className="fa fa-user"></i>
                </Link>
            </li>
        )
    }

    const renderSingOut = () => {
        return (
            <li className="nav-item mr-2">
                <Link
                    to='/'
                    onClick={logout}
                    data-value="sign-out"
                    className="nav-link"
                    title="Sign-out">
                    <i className="fa fa-sign-out"></i>
                </Link>
            </li>
        )
    }

    return (
        <nav className="navbar navbar-expand-sm fixed-top justify-content-center">
            <div className="navbar-inner d-flex font-weight-bold justify-content-between">
                <Link to='/' className="navbar-brand">
                    <i className="fa fa-home">
                        HOME</i>
                </Link>
                <ul className="navbar-nav flex-row">
                    {renderProfile()}
                    {isAuth && renderSingOut()}
                </ul>
            </div>
        </nav>
    )
}

NavBar.propTypes = {
    isAuth: PropTypes.bool,
    logout: PropTypes.func
}