import React from 'react';
import PropTypes from 'prop-types';

import './profile.css';
import avatar from './avatarDefault.png';

export default function Profile({username, title}) {
    return (
        <section className="profile">
            <div className="container mt-2">
                <div className="jumbotron mb-2 ">
                    <h2 className="text-capitalize text-info mb-5 text-center">{title}</h2>
                    <div className="image">
                        <img src={avatar} alt="avatar"/>
                    </div>
                    <p className="text-center mt-4">Your Name: {username}</p>
                </div>
            </div>
        </section>
    )
}

Profile.propTypes = {
    username: PropTypes.string,
    title: PropTypes.string
}