import React from 'react'
import {Route, Redirect} from 'react-router-dom'
import {connect} from 'react-redux'

const PrivateRoute = ({
    component: Component,
    ...rest
}) => {
    return (
        <Route
            {...rest}
            render={props => rest.isAuth
            ? (<Component {...props}/>)
            : (<Redirect
                to={{
                pathname: '/registration',
                state: {
                    from: props.location
                }
            }}/>)}/>
    )
}

function mapStateToProps({isAuth}) {
    return {isAuth};
}

export default connect(
    mapStateToProps,
    null
)(PrivateRoute)