export const getMockData = (load) => {

    const mockParam = {
        img: 'https://i1.rozetka.ua/goods/11488190/samsung_sm_a505fzbqsek_images_11488190360.j' +
                'pg',
        title: 'Samsung Galaxy A50 6/128GB Blue',
        text: 'Экран (6.4", Super AMOLED, 2340x1080)/ Samsung Exynos 9610 (2.3 ГГц + 1.7 ГГц)/ ' +
                'тройная основная камера: 25 Мп + 5 Мп + 8 Мп, фронтальная камера: 25 Мп/ RAM 6 Г' +
                'Б/ 128 ГБ встроенной памяти + microSD (до 512 ГБ)/ 3G/ LTE/ GPS/ ГЛОНАСС/ поддер' +
                'жка 2х SIM-карт (Nano-SIM)/ Android 9.0 (Pie)/ 4000 мА*ч'
    }

    const collArr = (item) => {
        item["img"] = mockParam["img"];
        item["title"] = mockParam["title"];
        item["text"] = mockParam["text"];
        return item;
    }
    return load.map((item) => collArr(item))
}