// REGISTRATION

export const registrationRequested = () => {
    return {type: REGISTRATION_REQUEST};
};
export const registrationLoaded = (data, username) => {
    return {
        type: REGISTRATION_SUCCESS,
        payload: {
            token: data.token,
            success: data.success,
            username
        }
    };
};
export const registrationError = (error) => {
    return {
        type: REGISTRATION_FAILURE,
        payload: {
            data: error
        }
    };
};

// LOGOUT

export const logoutCreate = () => {
    return {type: LOGOUT};
};

